import React from "react";

import { Container, Row, Col, Form, ListGroup } from "reactstrap";
import { useParams } from "react-router-dom";
import tourData from "../assets/data/tours";
import CalculateAvgRating from "../utils/calculateAvgRating";

import avatar from "../assets/images/avatar.jpg";
import "../styles/tourdetails.css";

const TourDetails = () => {
  const { id } = useParams();

  //this is currently static data
  //later we will call API from here, and load data from db
  const tour = tourData.find((tour) => tour.id === id);

  //destructuring properties from tour object
  const {
    photo,
    title,
    desc,
    price,
    reviews,
    city,
    distance,
    address,
    maxGroupSize,
  } = tour;

  const { totalRating, avgRating } = CalculateAvgRating(reviews);

  //format date
  const options = { day: "numeric", month: "long", year: "numeric" };

  return (
    <>
      <Container>
        <Row>
          <Col lg="8">
            <div className="tour__content">
              <img src={photo} alt="" />

              <div className="tour__info">
                <h2>{title}</h2>

                <div className="d-flex align-items-center gap-5">
                  <span className="tour__rating d-flex align-items-center gap-1">
                    <i
                      class="ri-star-fill"
                      style={{ color: "rgb(236, 112, 112)" }}
                    ></i>
                    {avgRating === 0 ? null : avgRating}
                    {totalRating === 0 ? (
                      "Not rated"
                    ) : (
                      <span>({reviews?.length})</span>
                    )}
                  </span>

                  <span>
                    <i class="ri-map-pin-user-fill"></i>
                    {address}
                  </span>
                </div>

                <div className="tour__extra-details">
                  <span>
                    {" "}
                    <i class="ri-map-pin-fill"></i>
                    {city}
                  </span>
                  <span>
                    <i class="ri-money-dollar-circle-line"></i>${price} /per
                    person
                  </span>
                  <span>
                    <i class="ri-group-line"></i>
                    {maxGroupSize}
                  </span>
                </div>
                <h5>Description</h5>
                <p>{desc}</p>
              </div>

              {/* __________________tour review section starts_________________ */}

              <div className="tour__reviews mt-4">
                <h4>Reviews ({reviews?.length})</h4>

                <Form>
                  <div className="rating__group d-flex align-items-center gap-3 mb-4">
                    1<i class="ri-star-fill"></i>2<i class="ri-star-fill"></i>3
                    <i class="ri-star-fill"></i>4<i class="ri-star-fill"></i>5
                    <i class="ri-star-fill"></i>
                  </div>

                  <div className="review__input">
                    <input type="text" placeholder="Share your thoughts" />
                    <button
                      className="btn primary__btn text-white"
                      type="submit"
                    >
                      Submit
                    </button>
                  </div>
                </Form>

                <ListGroup className="user_reviews ">
                  {reviews?.map((reviews) => (
                    <div className="review__item">
                      <img src={avatar} alt="img" />

                      <div className="w-100">
                        <div
                          className="d-flex align-items-center
                              justify-content-between"
                        >
                          <div>
                            <h5>Joy</h5>
                            <p>
                              {new Date("09-02-2023").toLocaleDateString(
                                "en-US",options
                              )}
                            </p>
                          </div>
                          <span className="d-flex align-items-center">
                              5 
                          </span>
                        </div>
                      </div>
                    </div>
                  ))}
                </ListGroup>
              </div>

              {/* __________________tour review section ends_________________ */}
            </div>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default TourDetails;
