import React from "react";

import { Container, Row, Col, Form, ListGroup } from "reactstrap";
import { useParams } from "react-router-dom";
import tourData from "../assets/data/tours";
import CalculateAvgRating from "../utils/calculateAvgRating";
import "../styles/tourdetails.css";

const TourDetails = () => {
  const { id } = useParams();

  //this is currently static data
  //later we will call API from here, and load data from db
  const tour = tourData.find((tour) => tour.id === id);

  //destructuring properties from tour object
  const {
    photo,
    title,
    desc,
    price,
    reviews,
    city,
    distance,
    address,
    maxGroupSize,
  } = tour;

  const { totalRating, avgRating } = CalculateAvgRating(reviews);
  return (
    <>
      <Container>
        <Row>
          <Col lg="8">
            <div className="tour__content">
              <img src={photo} alt="" />

              <div className="tour__info">
                <h2>{title}</h2>

                <div className="d-flex align-items-center gap-5">
                  <span className="tour__rating d-flex align-items-center gap-1">
                    <i
                      class="ri-star-fill"
                      style={{ color: "rgb(236, 112, 112)" }}
                    ></i>
                    {avgRating === 0 ? null : avgRating}
                    {totalRating === 0 ? (
                      "Not rated"
                    ) : (
                      <span>({reviews?.length})</span>
                    )}
                  </span>

                  <span>
                    <i class="ri-map-pin-user-fill"></i>
                    {address}
                  </span>
                </div>

                <div className="tour__extra-details">
                  <span>
                    {" "}
                    <i class="ri-map-pin-fill"></i>
                    {city}
                  </span>
                  <span>
                    <i class="ri-money-dollar-circle-line"></i>${price} /per
                    person
                  </span>
                  <span>
                    <i class="ri-group-line"></i>
                    {maxGroupSize}
                  </span>
                </div>
                <h5>Description</h5>
                <p>{desc}</p>
              </div>

              {/* __________________tour review section starts_________________ */}

              <div className="tour__reviews mt-4">
                <h4>Reviews ({reviews?.length})</h4>

                <Form>
                  <div className="d-flex align-items-center gap-3 mb-4 rating__group">
                    1<i class="ri-star-fill"></i>
                    2<i class="ri-star-fill"></i>
                    3<i class="ri-star-fill"></i>
                    4<i class="ri-star-fill"></i>
                    5<i class="ri-star-fill"></i>
                  </div>
                </Form>
              </div>

              {/* __________________tour review section ends_________________ */}
            </div>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default TourDetails;
