import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "reactstrap";
import CommonSection from "../shared/CommonSection";

import tourData from "../assets/data/tours.js";
import TourCard from "./../shared/TourCard";
import Newsletter from "./../shared/Newsletter";
import SearchBar from "./../shared/SearchBar";

import useFetch from '../hooks/useFetch'
import { BASE_URL } from "../utils/config";

import "../styles/tours.css";

const Tours = () => {
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);

  const {data:tours} = useFetch(`${BASE_URL}/tours`);
  const {data:tourCount} = useFetch(`${BASE_URL}/tours/seacrh/getTourCount`);

  useEffect(() => {
    const pages = Math.ceil(tourCount / 4); //later we will use backend data count
    setPageCount(pages);
  }, [page, tourCount]);

  return (
    <>
      <CommonSection title={"All Tours"} />
      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>
      <section className="pt-0">
        <Container>
          <Row>
            {tourData?.map((tour) => (
              <Col lg="3" key={tour.id} className="mb-4">
                <TourCard tour={tour} />
              </Col>
            ))}

            <Col lg="12">
              <div
                className="pagination d-flex align-items-center
               justify-content-center mt-4 gap-3"
              >
                {[...Array(pageCount).keys()].map((number) => (
                  <span
                    key={number}
                    onClick={() => setPage(number)}
                    className={page === number ? "active__page" : ""}
                  >
                    {number + 1}
                  </span>
                ))}
              </div>
            </Col>
            
          </Row>
          
        </Container>
      </section>
      <Newsletter/>
    </>
  );
};
export default Tours;
